import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.*;


class LibraryTest {

    @Test
    void test1() {
        fooTest(10, 5, 2, 0);
    }

    @Test
    void test2() {
        fooTest(5, 10, 20, 0);
    }

    private void fooTest(int a, int b, int c, int expectedResult) {
        Library library = new Library();
        assertEquals(expectedResult, library.foo(a, b, c));
    }
}
